pragma solidity ^0.5.0;

contract Marketplace {
    string public name;
    uint public productCount = 0;
    mapping(uint => Product) public products;


    struct Product {
        uint id;
        string name;
        uint price;
        address payable owner;
        bool purchased;
    }

    event ProductCreated(uint id, string name, uint price, address payable owner, bool purchased);

    event ProductPurchased(uint id, string name, uint price, address payable owner, bool purchased);

    constructor() public {
        name = "Simsies Marketplace";
    }

    function createProduct(string memory _name, uint _price) public {
        // validation
        require(bytes(_name).length > 0);
        require(_price > 0);

        // creation logic
        productCount++;
        // create product
        products[productCount] = Product(productCount, _name, _price, msg.sender, false);

        // trigger event
        emit ProductCreated(productCount, _name, _price, msg.sender, false);
    }

// payable allows transfer and accepts a value
    function purchaseProduct(uint _id) public payable {
        //memory keyword copies to memory instead of editing blockchain
        Product memory _product = products[_id];
        address payable _seller = _product.owner;

        // validation
        require(_product.purchased == false);
        require(_product.id > 0 && _product.id < productCount);
        require(msg.value >= _product.price);
        require(_seller != msg.sender);

        // transfer ownership to buyer
        _product.owner = msg.sender;
        // mark product as purchased
        _product.purchased = true;

        // update in mapping
        products[_id] = _product;

        // pay seller by sending them Ether :)
        address(_seller).transfer(msg.value);

        emit ProductPurchased(productCount, _product.name, _product.price, msg.sender, true);
    }
}
